import React, { useState, useEffect } from 'react';

const API_URL = 'http://localhost:5000';

const ClickerGame = () => {
  const [totalScore, setTotalScore] = useState(0);
  const [clickNotification, setClickNotification] = useState(null);
  const [prizes, setPrizes] = useState([]);
  const [userId, setUserId] = useState(null);

  useEffect(() => {
    const storedUserId = localStorage.getItem('userId');
    setUserId(storedUserId || `user_${Date.now()}`);
    localStorage.setItem('userId', storedUserId || `user_${Date.now()}`);
  }, []);

  const handleClick = async () => {
    try {
      const response = await fetch(`${API_URL}/api/click`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ userId })
      });

      const { totalScore, scoreIncrease, prize } = await response.json();

      setTotalScore(totalScore);

      // Click Notification Animation
      const notification = {
        value: scoreIncrease,
        id: Date.now(),
        x: Math.random() * 20 - 10,
        y: Math.random() * 20 - 10
      };
      setClickNotification(notification);

      // Prizes Tracking
      if (prize) {
        setPrizes(prev => [prize, ...prev]);
      }

      // Remove notification after animation
      setTimeout(() => setClickNotification(null), 1000);
    } catch (error) {
      console.error('Click failed', error);
    }
  };

  const removePrize = (indexToRemove) => {
    setPrizes(prev => prev.filter((_, index) => index !== indexToRemove));
  };

  const clearAllPrizes = () => {
    setPrizes([]);
  };

  return (
    <div style={{ 
      display: 'flex', 
      width: '100%', 
      height: '100vh',
      backgroundColor: '#f0f0f0'
    }}>
      {/* Left Side - Click Area */}
      <div style={{ 
        width: '50%', 
        display: 'flex', 
        flexDirection: 'column', 
        alignItems: 'center', 
        justifyContent: 'center' 
      }}>
        <h2>{totalScore} cookies</h2>
        <div style={{ position: 'relative' }}>
          <button 
            onClick={handleClick}
            style={{
              padding: '15px 30px',
              fontSize: '18px',
              backgroundColor: '#4CAF50',
              color: 'white',
              border: 'none',
              borderRadius: '10px',
              cursor: 'pointer'
            }}
          >
            Cookie!
          </button>

          {clickNotification && (
            <div style={{
              position: 'absolute',
              top: '50%',
              left: '50%',
              transform: `translate(${clickNotification.x}px, ${clickNotification.y}px)`,
              color: 'white',
              opacity: 0,
              animation: 'fadeOut 3s upwards',
              fontSize: '24px'
            }}>
              +{clickNotification.value}
            </div>
          )}
        </div>
      </div>

      {/* Right Side - Prizes */}
      <div style={{ 
        width: '50%', 
        backgroundColor: '#e0e0e0', 
        padding: '20px', 
        overflowY: 'auto' 
      }}>
        <div style={{ 
          display: 'flex', 
          justifyContent: 'space-between', 
          alignItems: 'center' 
        }}>
          <h2>Prizes Won</h2>
          <button 
            onClick={clearAllPrizes}
            style={{
              backgroundColor: 'red',
              color: 'white',
              border: 'none',
              padding: '5px 10px',
              borderRadius: '5px'
            }}
          >
            Clear All
          </button>
        </div>
        {prizes.map((prize, index) => (
          <div 
            key={index} 
            style={{
              backgroundColor: 'white',
              margin: '10px 0',
              padding: '10px',
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              borderRadius: '5px'
            }}
          >
            {prize}
            <button 
              onClick={() => removePrize(index)}
              style={{
                backgroundColor: 'red',
                color: 'white',
                border: 'none',
                borderRadius: '50%',
                width: '25px',
                height: '25px',
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center'
              }}
            >
              âœ•
            </button>
          </div>
        ))}
      </div>

      <style>{`
        @keyframes fadeOut {
          0% { opacity: 1; transform: translate(0, 0); }
          100% { opacity: 0; transform: translate(var(--x), var(--y)); }
        }
      `}</style>
    </div>
  );
};

export default ClickerGame;
