import React, { useState, useEffect } from 'react';

const API_URL = 'http://localhost:5000';  // Backend server URL

const ClickerGame = () => {
  const [totalScore, setTotalScore] = useState(0);
  const [lastScoreIncrease, setLastScoreIncrease] = useState(0);
  const [userId, setUserId] = useState(null);
  const [lastPrize, setLastPrize] = useState(null);

  useEffect(() => {
    const storedUserId = localStorage.getItem('userId');
    if (storedUserId) {
      setUserId(storedUserId);
    } else {
      const newUserId = `user_${Date.now()}`;
      setUserId(newUserId);
      localStorage.setItem('userId', newUserId);
    }
  }, []);

  useEffect(() => {
    if (userId) {
      fetchUserStats();
    }
  }, [userId]);

  const fetchUserStats = async () => {
    try {
      const response = await fetch(`${API_URL}/api/stats/${userId}`);
      const data = await response.json();
      setTotalScore(data.totalScore || 0);
    } catch (error) {
      console.error('Failed to fetch stats', error);
    }
  };

  const handleClick = async () => {
    try {
      const response = await fetch(`${API_URL}/api/click`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ userId })
      });

      // Add error handling for non-JSON responses
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      const { totalScore, scoreIncrease, prize } = data;

      setTotalScore(totalScore);
      setLastScoreIncrease(scoreIncrease);

      if (prize) {
        setLastPrize(prize);
        alert(`Prize Won: ${prize}`);
      }
    } catch (error) {
      console.error('Click failed', error);
      alert('Failed to process click. Check server connection.');
    }
  };

  return (
    <div style={{ 
      width: '300px', 
      margin: '20px auto', 
      textAlign: 'center',
      padding: '20px',
      border: '1px solid #ddd',
      borderRadius: '8px',
      boxShadow: '0 4px 6px rgba(0,0,0,0.1)'
    }}>
      <h2>Cookie Clicker Clone</h2>
      <p>Total Score: {totalScore}</p>

      {lastPrize && (
        <div style={{ 
          backgroundColor: '#ffeb3b', 
          color: 'black',
          padding: '10px',
          marginBottom: '10px',
          borderRadius: '4px'
        }}>
          Prize Won: {lastPrize}
        </div>
      )}

      <button 
        onClick={handleClick}
        style={{
          backgroundColor: '#ff6b6b',
          color: 'white',
          border: 'none',
          padding: '10px 20px',
          borderRadius: '5px',
          cursor: 'pointer',
          transform: 'scale(1.05)',
          transition: 'transform 0.1s'
        }}
      >
        Click Me! (+{lastScoreIncrease} Points)
      </button>
    </div>
  );
};

export default ClickerGame;
