import React, { useState, useEffect, useRef } from 'react';

const API_URL = 'http://localhost:5000';

const CookieIcon = ({ isPressed, onClick }) => (
  <svg 
    onClick={onClick}
    style={{
      cursor: 'pointer',
      transform: isPressed ? 'scale(0.9)' : 'scale(1)',
      transition: 'transform 0.1s',
      filter: isPressed ? 'brightness(0.8)' : 'none'
    }}
    width="200" 
    height="200" 
    viewBox="0 0 100 100"
  >
    <circle 
      cx="50" 
      cy="50" 
      r="45" 
      fill="#D2691E"
    />

    {[
      {cx: 30, cy: 25}, 
      {cx: 70, cy: 35}, 
      {cx: 40, cy: 60}, 
      {cx: 60, cy: 65}
    ].map(({cx, cy}, index) => (
      <circle 
        key={index} 
        cx={cx} 
        cy={cy} 
        r="5" 
        fill="#8B4513"
      />
    ))}
  </svg>
);

const ClickerGame = () => {
  const [totalScore, setTotalScore] = useState(0);
  const [clickNotification, setClickNotification] = useState(null);
  const [prizes, setPrizes] = useState([]);
  const [userId, setUserId] = useState(null);
  const [isPressed, setIsPressed] = useState(false);
  const [activeEffect, setActiveEffect] = useState(null);
  const effectTimerRef = useRef(null);
  const effectEndTimeRef = useRef(null);

  useEffect(() => {
    const storedUserId = localStorage.getItem('userId');
    setUserId(storedUserId || `user_${Date.now()}`);
    localStorage.setItem('userId', storedUserId || `user_${Date.now()}`);
  }, []);

  // Effect tracking and cleanup
  useEffect(() => {
    if (activeEffect) {
      // Clear any existing timer
      if (effectTimerRef.current) {
        clearInterval(effectTimerRef.current);
      }

      // Set end time for time-based effects
      if (activeEffect.remainingTime !== undefined) {
        effectEndTimeRef.current = Date.now() + (activeEffect.remainingTime * 1000);

        effectTimerRef.current = setInterval(() => {
          const remainingTime = Math.max(0, Math.ceil((effectEndTimeRef.current - Date.now()) / 1000));

          if (remainingTime <= 0) {
            clearInterval(effectTimerRef.current);
            setActiveEffect(null);
          } else {
            setActiveEffect(prev => ({ 
              ...prev, 
              remainingTime 
            }));
          }
        }, 1000);
      }

      // Cleanup function
      return () => {
        if (effectTimerRef.current) {
          clearInterval(effectTimerRef.current);
        }
      };
    }
  }, [activeEffect]);

  const handleClick = async () => {
    setIsPressed(true);
    setTimeout(() => setIsPressed(false), 100);

    // Check if an effect is active and block new effects
    if (activeEffect) {
      // Existing click logic during an active effect
      try {
        const response = await fetch(`${API_URL}/api/click`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ 
            userId, 
            activeEffect: {
              type: activeEffect.type,
              remainingTime: activeEffect.remainingTime,
              remainingClicks: activeEffect.remainingClicks
            }
          })
        });

        const { totalScore, scoreIncrease } = await response.json();

        console.log(totalScore, scoreIncrease);

        setTotalScore(totalScore);

        // Notifications for base click
        setClickNotification([{
          value: scoreIncrease,
          id: Date.now(),
          x: Math.random() * 20 - 10,
          y: Math.random() * 20 - 10
        }]);

        // Update active effect if click-based
        if (activeEffect.remainingClicks !== undefined) {
          const newRemainingClicks = Math.max(0, activeEffect.remainingClicks - 1);
          if (newRemainingClicks === 0) {
            setActiveEffect(null);
          } else {
            setActiveEffect(prev => ({ 
              ...prev, 
              remainingClicks: newRemainingClicks 
            }));
          }
        }

        setTimeout(() => setClickNotification(null), 1000);
      } catch (error) {
        console.error('Click failed', error);
      }
      return;
    }

    // Normal click logic when no effect is active
    try {
      const response = await fetch(`${API_URL}/api/click`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ 
          userId,
          prizeProbabilityMultiplier: 0.05 // 5% prize chance
        })
      });

      const { totalScore, scoreIncrease, prize, prizeEffect } = await response.json();

      setTotalScore(totalScore);

      const notifications = [];

      // Base click notification
      notifications.push({
        value: scoreIncrease,
        id: Date.now(),
        x: -20,
        y: Math.random() * 20 - 10
      });

      // Prize notification if exists
      if (prize && prizeEffect) {
        notifications.push({
          value: prizeEffect.pointIncrease,
          prize: prize,
          id: Date.now() + 1,
          x: 20,
          y: Math.random() * 20 - 10
        });

        // Handle different prize effects
        switch(prize) {
          case 'Double Points Boost':
            setActiveEffect({
              type: 'Double Points',
              remainingTime: 30,
              startTime: Date.now()
            });
            break;
          case 'Prize Multiplier':
            setActiveEffect({
              type: 'Prize Multiplier',
              remainingClicks: 10
            });
            break;
          case 'Lucky Charm':
            setActiveEffect({
              type: 'Lucky Charm',
              remainingClicks: 5
            });
            break;
        }

        // Add to prizes list
        setPrizes(prev => [{
          name: prize,
          timestamp: new Date().toLocaleString()
        }, ...prev]);
      }

      setClickNotification(notifications);

      setTimeout(() => setClickNotification(null), 1000);
    } catch (error) {
      console.error('Click failed', error);
    }
  };

  const removePrize = (indexToRemove) => {
    setPrizes(prev => prev.filter((_, index) => index !== indexToRemove));
  };

  const clearAllPrizes = () => {
    setPrizes([]);
  };

  return (
    <div style={{ 
      display: 'flex', 
      width: '100%', 
      height: '100vh',
      backgroundColor: '#f0f0f0'
    }}>
      <div style={{ 
        width: '50%', 
        display: 'flex', 
        flexDirection: 'column', 
        alignItems: 'center', 
        justifyContent: 'center' 
      }}>
        <h2>{totalScore} cookies </h2>

        {/* Effect Notification Area */}
        {activeEffect && (
          <div style={{
            color: 'green',
            marginBottom: '10px',
            fontWeight: 'bold'
          }}>
            {activeEffect.type} Activated!
          </div>
        )}

        <div style={{ position: 'relative' }}>
          <CookieIcon 
            isPressed={isPressed}
            onClick={handleClick} 
          />

          {clickNotification && clickNotification.map((notification, index) => (
            <div 
              key={notification.id} 
              style={{
                position: 'absolute',
                // Position prize notifications differently from regular click notifications
                top: notification.prize ? '20%' : '50%', 
                left: notification.prize ? '60%' : '50%',
                transform: `translate(${notification.x}px, ${notification.y}px)`,
                color: notification.prize ? 'gold' : 'green',
                opacity: 0,
                animation: 'fadeOut 3s forwards',
                fontSize: '24px',
                textAlign: 'center',
                fontWeight: notification.prize ? 'bold' : 'normal'
              }}
            >
              {notification.prize 
                ? `ðŸŽ‰+${notification.value}` 
                : `+${notification.value}`
              }
            </div>
          ))}
        </div>


        {/* Effect Counter */}
        {activeEffect && (
          <div style={{
            marginTop: '10px',
            fontSize: '18px',
            color: 'blue'
          }}>
            {activeEffect.remainingTime !== undefined 
              ? `${activeEffect.type} ends in: ${activeEffect.remainingTime} seconds`
              : `${activeEffect.type} remaining: ${activeEffect.remainingClicks} clicks`
            }
          </div>
        )}
      </div>

      {/* Prize List */}
      <div style={{ 
        width: '50%', 
        backgroundColor: '#e0e0e0', 
        padding: '20px', 
        overflowY: 'auto' 
      }}>
        <div style={{ 
          display: 'flex', 
          justifyContent: 'space-between', 
          alignItems: 'center' 
        }}>
          <h2>Prizes Won</h2>
          <button 
            onClick={clearAllPrizes}
            style={{
              backgroundColor: 'red',
              color: 'white',
              border: 'none',
              padding: '5px 10px',
              borderRadius: '5px'
            }}
          >
            Clear All
          </button>
        </div>
        {prizes.map((prize, index) => (
          <div 
            key={index} 
            style={{
              backgroundColor: 'white',
              margin: '10px 0',
              padding: '10px',
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              borderRadius: '5px'
            }}
          >
            {prize.name}
            <button 
              onClick={() => removePrize(index)}
              style={{
                backgroundColor: 'red',
                color: 'white',
                border: 'none',
                borderRadius: '50%',
                width: '25px',
                height: '25px',
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center'
              }}
            >
              âœ•
            </button>
          </div>
        ))}
      </div>

      <style>{`
        @keyframes fadeOut {
          0% { opacity: 1; transform: translate(0, 0); }
          100% { opacity: 0; transform: translate(var(--x), var(--y)); }
        }
      `}</style>
    </div>
  );
};

export default ClickerGame;
