import React, { useState, useEffect } from 'react';

const API_URL = 'http://localhost:5000';

const CookieIcon = ({ isPressed, onClick }) => (
  <svg 
    onClick={onClick}
    style={{
      cursor: 'pointer',
      transform: isPressed ? 'scale(0.9)' : 'scale(1)',
      transition: 'transform 0.1s',
      filter: isPressed ? 'brightness(0.8)' : 'none'
    }}
    width="200" 
    height="200" 
    viewBox="0 0 100 100"
  >
    {/* Circular cookie base */}
    <circle 
      cx="50" 
      cy="50" 
      r="45" 
      fill="#D2691E"  // Chocolate brown
    />

    {/* Cookie chips */}
    {[
      {cx: 30, cy: 25}, 
      {cx: 70, cy: 35}, 
      {cx: 40, cy: 60}, 
      {cx: 60, cy: 65}
    ].map(({cx, cy}, index) => (
      <circle 
        key={index} 
        cx={cx} 
        cy={cy} 
        r="5" 
        fill="#8B4513"  // Dark brown chips
      />
    ))}
  </svg>
);

const ClickerGame = () => {
  const [totalScore, setTotalScore] = useState(0);
  const [clickNotification, setClickNotification] = useState(null);
  const [prizes, setPrizes] = useState([]);
  const [userId, setUserId] = useState(null);
  const [isPressed, setIsPressed] = useState(false);

  useEffect(() => {
    const storedUserId = localStorage.getItem('userId');
    setUserId(storedUserId || `user_${Date.now()}`);
    localStorage.setItem('userId', storedUserId || `user_${Date.now()}`);
  }, []);

  const handleClick = async () => {
    setIsPressed(true);
    setTimeout(() => setIsPressed(false), 100);

    try {
      const response = await fetch(`${API_URL}/api/click`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ userId })
      });

      const { totalScore, scoreIncrease, prize, prizeEffect } = await response.json();
      console.log(prize, totalScore, scoreIncrease, prizeEffect);

      setTotalScore(totalScore);

      const notifications = [];

      // Always add base score increase notification
      notifications.push({
        value: scoreIncrease,
        id: Date.now(),
        x: Math.random() * 20 - 10,
        y: Math.random() * 20 - 10
      });

      // Add prize notification if prize exists
      if (prize && prizeEffect) {
        notifications.push({
          value: prizeEffect.pointIncrease,
          prize: prize,
          id: Date.now() + 1,
          x: Math.random() * 20 - 10,
          y: Math.random() * 20 - 10
        });

        setPrizes(prev => [prize, ...prev]);
      }

      setClickNotification(notifications);

      setTimeout(() => setClickNotification(null), 1000);
    } catch (error) {
      console.error('Click failed', error);
    }
  };

  const removePrize = (indexToRemove) => {
    setPrizes(prev => prev.filter((_, index) => index !== indexToRemove));
  };

  const clearAllPrizes = () => {
    setPrizes([]);
  };

  return (
    <div style={{ 
      display: 'flex', 
      width: '100%', 
      height: '100vh',
      backgroundColor: '#f0f0f0'
    }}>
      {/* Left Side - Click Area */}
      <div style={{ 
        width: '50%', 
        display: 'flex', 
        flexDirection: 'column', 
        alignItems: 'center', 
        justifyContent: 'center' 
      }}>
        <h2>{totalScore} cookies </h2>
        <div style={{ position: 'relative' }}>
          <CookieIcon 
            isPressed={isPressed}
            onClick={handleClick} 
          />

          {clickNotification && clickNotification.map((notification, index) => (
            <div key={notification.id} style={{
              position: 'absolute',
              top: '50%',
              left: '50%',
              transform: `translate(${notification.x}px, ${notification.y}px)`,
              color: 'white',
              opacity: 0,
              animation: 'fadeOut 3s forwards',
              fontSize: '24px',
              textAlign: 'center'
            }}>
              {notification.prize 
                ? `+${notification.value}`
                : `+${notification.value}`
              }
            </div>
          ))}
        </div>
      </div>

      {/* Right Side - Prizes */}
      <div style={{ 
        width: '50%', 
        backgroundColor: '#e0e0e0', 
        padding: '20px', 
        overflowY: 'auto' 
      }}>
        <div style={{ 
          display: 'flex', 
          justifyContent: 'space-between', 
          alignItems: 'center' 
        }}>
          <h2>Prizes Won</h2>
          <button 
            onClick={clearAllPrizes}
            style={{
              backgroundColor: 'red',
              color: 'white',
              border: 'none',
              padding: '5px 10px',
              borderRadius: '5px'
            }}
          >
            Clear All
          </button>
        </div>
        {prizes.map((prize, index) => (
          <div 
            key={index} 
            style={{
              backgroundColor: 'white',
              margin: '10px 0',
              padding: '10px',
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              borderRadius: '5px'
            }}
          >
            {prize} + `${notification.value}  cookies!`
            <button 
              onClick={() => removePrize(index)}
              style={{
                backgroundColor: 'red',
                color: 'white',
                border: 'none',
                borderRadius: '50%',
                width: '25px',
                height: '25px',
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center'
              }}
            >
              âœ•
            </button>
          </div>
        ))}
      </div>

      <style>{`
        @keyframes fadeOut {
          0% { opacity: 1; transform: translate(0, 0); }
          100% { opacity: 0; transform: translate(var(--x), var(--y)); }
        }
      `}</style>
    </div>
  );
};

export default ClickerGame;
